","keccak256":"0x6400c4bee15052e043e5d10315135972529bd1c8012f43da494dc6b4f4661058","license":"MIT"},"contracts/airdrop/AirdropRewarderS2.sol":{"content":"// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.20;

import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {ERC20Upgradeable} from "@openzeppelin/contracts-upgradeable/token/ERC20/ERC20Upgradeable.sol";
import {MerkleProof} from "@openzeppelin/contracts/utils/cryptography/MerkleProof.sol";
import {Initializable} from "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import {OwnableUpgradeable} from "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import {IVestedZeroNFT} from "contracts/interfaces/IVestedZeroNFT.sol";

contract AirdropRewarderS2 is Initializable, OwnableUpgradeable {
    using SafeERC20 for ERC20Upgradeable;

    bytes32 private merkleRoot;

    mapping(address => bool) public rewardsClaimed;
    mapping(address => bool) public vestRewards;

    ERC20Upgradeable public rewardToken;

    IVestedZeroNFT public vestedZeroNFT;
    uint256 public unlockDate;
    uint256 public endDate;
    bool public paused;

    error InvalidAddress();
    error InvalidLockDuration();
    error RewardsAlreadyClaimed();
    error ClaimNotReady();
    error ClaimDurationOver();
    error InvalidMerkleProof(bytes32[]);

    event MerkleRootSet(bytes32 oldMerkleRoot, bytes32 newMerkleRoot);
    event RewardTokenSet(address oldRewardToken, address newRewardToken);
    event LockerSet(address oldLocker, address newLocker);
    event RewardsClaimed(address _user, uint256 _rewardsAmount);

    event RewardsVested(address _user, uint256 _lockAmount);
    event RewardsTransferred(address _user, uint256 _transferAmount);
    event RewardTerminated();

    modifier whenNotPaused() {
        require(!paused, "AirdropRewarder: Claims are paused");
        _;
    }

    constructor() {
        _disableInitializers();
    }

    function initialize(
        address _rewardToken,
        address _vestedZeroNFT,
        uint256 _unlockDate,
        uint256 _endDate,
        address _owner,
        address[] memory vestRewardsForUsers
    ) external initializer {
        __Ownable_init(_owner);
        unlockDate = _unlockDate;
        endDate = _endDate;
        vestedZeroNFT = IVestedZeroNFT(_vestedZeroNFT);
        rewardToken = ERC20Upgradeable(_rewardToken);

        for (uint256 i = 0; i < vestRewardsForUsers.length; i++) {
            vestRewards[vestRewardsForUsers[i]] = true;
        }
    }

    function setMerkleRoot(bytes32 _merkleRoot) external onlyOwner {
        emit MerkleRootSet(_merkleRoot, merkleRoot);
        merkleRoot = _merkleRoot;
    }

    function setVestForUsers(address[] memory _user, bool _vest) external onlyOwner {
        for (uint256 i = 0; i < _user.length; i++) {
            vestRewards[_user[i]] = _vest;
        }
    }

    function setRewardToken(address _rewardToken) external onlyOwner {
        if (_rewardToken == address(0)) revert InvalidAddress();
        emit RewardTokenSet(address(rewardToken), _rewardToken);
        rewardToken = ERC20Upgradeable(_rewardToken);
    }

    function setVestedZeroNFT(address _vestedZeroNFT) external onlyOwner {
        if (_vestedZeroNFT == address(0)) revert InvalidAddress();
        vestedZeroNFT = IVestedZeroNFT(_vestedZeroNFT);
    }

    function pauseClaims() external onlyOwner {
        paused = true;
    }

    function unpauseClaims() external onlyOwner {
        paused = false;
    }

    function claim(address _user, uint256 _claimAmount, bytes32[] calldata _merkleProofs) external whenNotPaused {
        if (_user == address(0)) revert InvalidAddress();
        if (block.timestamp < unlockDate) revert ClaimNotReady();
        if (block.timestamp > endDate) revert ClaimDurationOver();

        bytes32 node = keccak256(abi.encodePacked(_user, _claimAmount));

        if (!MerkleProof.verify(_merkleProofs, merkleRoot, node)) {
            revert InvalidMerkleProof(_merkleProofs);
        }

        if (rewardsClaimed[_user]) revert RewardsAlreadyClaimed();

        rewardsClaimed[_user] = true;

        if (vestRewards[_user]) {
            rewardToken.approve(address(vestedZeroNFT), _claimAmount);
            vestedZeroNFT.mint(_user, _claimAmount, 0, 31 days, 0, 0, false, IVestedZeroNFT.VestCategory.AIRDROP);
            emit RewardsVested(_user, _claimAmount);
        } else {
            rewardToken.safeTransfer(_user, _claimAmount);
            emit RewardsTransferred(_user, _claimAmount);
        }

        emit RewardsClaimed(_user, _claimAmount);
    }

    function adminWithdrawal() public onlyOwner {
        rewardToken.safeTransfer(_msgSender(), rewardToken.balanceOf(address(this)));
        emit RewardTerminated();
    }
}
","keccak256":"0x3704061ca807e3da187f3e31bbe05340351de48805371303fb9ec8f96eec58d2","license":"UNLICENSED"},"contracts/interfaces/IVestedZeroNFT.sol":{"content":"// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.12;

// import {IERC721} from "@openzeppelin/contracts/interfaces/IERC721.sol";

interface IVestedZeroNFT {
    enum VestCategory {
        PRIVATE_SALE,
        EARLY_ZERO,
        NORMAL,
        AIRDROP
    }

    struct LockDetails {
        uint256 cliffDuration;
        uint256 unlockDate;
        uint256 pendingClaimed;
        uint256 pending;
        uint256 upfrontClaimed;
        uint256 upfront;
        uint256 linearDuration;
        uint256 createdAt;
        bool hasPenalty;
        VestCategory category;
    }

    /// Mints a vesting nft for a user. This is a privileged function meant to only be called by a contract or a deployer
    /// @param who For whom we are vesting tokesn for
    /// @param pending How much tokens the user gets after the cliff is over during the linear vesting
    /// @param upfront How much tokens the user gets upfront (befoer the cliff after the unlockDate)
    /// @param linearDuration How long is the linear vesting
    /// @param cliffDuration How long is the cliff (normally 1 month)
    /// @param unlockDate When will this vest start
    /// @param hasPenalty Will the user get penalized for withdrawing early (Radiant-style punishements)
    /// @param category A read-only value just to keep track of a NFT.
    function mint(
        address who,
        uint256 pending,
        uint256 upfront,
        uint256 linearDuration,
        uint256 cliffDuration,
        uint256 unlockDate,
        bool hasPenalty,
        VestCategory category
    ) external returns (uint256);

    function togglePause() external;

    function freeze(uint256 tokenId, bool what) external;

    /// Used by an admin to bulk update the cliff duration and linear distribution for a vest
    /// @param tokenIds the nfts to update
    /// @param linearDurations the linear duration to update with
    /// @param cliffDurations the cliff duration to update with
    function updateCliffDuration(
        uint256[] memory tokenIds,
        uint256[] memory linearDurations,
        uint256[] memory cliffDurations
    ) external;

    /// How much ZERO tokens this vesting nft can claim
    /// @param tokenId the id of the nft contract
    /// @return upfront how much tokens upfront this nft can claim
    /// @return pending how much tokens in the linear vesting (after the cliff) this nft can claim
    function claimable(uint256 tokenId) external view returns (uint256 upfront, uint256 pending);

    /// Executes a claim of tokens for a given nft
    /// @param id the nft id to claim tokens for
    function claim(uint256 id) external returns (uint256 toClaim);

    /// How much tokens have been claimed so far
    /// @param tokenId the nft id
    function claimed(uint256 tokenId) external view returns (uint256);

    /// How much tokens haven't been claimed so far
    /// @param tokenId the nft id
    function unclaimed(uint256 tokenId) external view returns (uint256);

    /// Calculate how much penalty the user will have to pay
    /// @param tokenId the nft id
    function penalty(uint256 tokenId) external view returns (uint256);

    /// Splits a vesting NFT into smaller vests so that it can be easily traded
    /// @param tokenId The nft to split for
    /// @param fraction By how much bps the split should happen (10000 bps = 100%)
    function split(uint256 tokenId, uint256 fraction) external;

    /// In case the a nft gets sold in the OTC market, would be great if the team could
    /// claim fees against the vested tokens
    /// @param salePrice The price the asset is going to be sold for
    /// @return royaltyReceiver The address that will receive the royalties
    /// @return royaltyAmount The royalty amount
    function royaltyInfo(uint256, uint256 salePrice) external view returns (address, uint256);

    /// Claim unvested tokens by the bonus staking contract
    /// @param tokenId The nft id
    function claimUnvested(uint256 tokenId) external;

    /// Metadata helper for nft platforms like openzea
    /// @param tokenId The nft id
    function tokenURI(uint256 tokenId) external view returns (string memory);
}
